import React, { Component } from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';
import './App.css';

class App extends Component {
    constructor() {
        super()
        this.state = {
            corgis: [],
            searchField: ''
        }
    }
    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => {
                response.json();
            })
            .then(users => {
                this.setState({  });
            });
            // .then(users => {
            //     this.setState({ corgis: users })
            // });
    }
    onSearchChange= (event) => {
        this.setState({ searchField: event.target.value });
    }
    render() {
        const filteredCorigs = this.state.corgis.filter(corgis => {
            return corgis.name.toLowerCase().includes(this.state.searchField.toLowerCase());
        });
        return (
            <div className='tc'>
                <h1 className='bb pa2 f1'>CorgiFriends</h1>
                <SearchBox searchChange={this.onSearchChange} />
                <CardList corgis={filteredCorigs} />
            </div>
        );
    }
}

export default App;
